mapper.select:
select name, id, email
from user
where id = #{id} and pass = #{pass}

select name, id, email, position
from user
where id = #{id}

select token
from user
where id = #{id}

mapper.update:
update user
set token = #{token}
where id = #{id}

update user
set token = #{token, jdbcType=VARCHAR}
where id = #{id}
